import React, { useState } from "react";
import "./reset.css";
import "./App.css";

import Header from "./Components/Header/Header";
import Main from "./Components/Main/Main";
import Modal from "./Components/Modal/Modal";
import Footer from "./Components/Footer/Footer";

import data from "./beastData.json";

function App() {
  const [modal, setModal] = useState(false);
  const [modalContent, setModalContent] = useState({});
  const [beastData, setBeastData] = useState(data);
  const [searchQuery, setSearchQuery] = useState("");

  function handleModal(beast) {
    setModal(!modal);
    setModalContent(beast);
  }

  function closeModal() {
    setModal(!modal);
    setModalContent({});
  }

  function handleSearch(query) {
    setSearchQuery(query);
  }

  function handleBeasts(event) {
    let beastNum = parseInt(event.target.value);
    const filteredBeasts = data.filter((beast) => {
      const isHornMatch = beast.horns === beastNum || isNaN(beastNum);
      const query = searchQuery.toLowerCase();
      const title = beast.title.toLowerCase();
      const keyword = beast.keyword.toLowerCase();

      let titleIndex = 0;
      let keywordIndex = 0;

      for (let i = 0; i < query.length; i++) {
        const char = query.charAt(i);
        titleIndex = title.indexOf(char, titleIndex);
        keywordIndex = keyword.indexOf(char, keywordIndex);

        if (titleIndex === -1 && keywordIndex === -1) {
          return false;
        }
      }

      return isHornMatch && (isTitleMatch || isKeywordMatch);
    });

    event.target.value === ""
      ? setBeastData(data)
      : setBeastData(filteredBeasts);
  }

  return (
    <div className="App">
      <Header handleBeasts={handleBeasts} handleSearch={handleSearch} />
      <Main beastData={beastData} handleModal={handleModal} />
      {modal && <Modal modalContent={modalContent} closeModal={closeModal} />}
      <Footer />
    </div>
  );
}

export default App;
