import React, { useState } from "react";
import "./reset.css";
import "./App.css";

import Header from "./Components/Header/Header";
import Main from "./Components/Main/Main";
import Modal from "./Components/Modal/Modal";
import Footer from "./Components/Footer/Footer";

import data from "./beastData.json";

function App() {
  const [modal, setModal] = useState(false);
  const [modalContent, setModalContent] = useState({});
  const [beastData, setBeastData] = useState(data);
  const [searchQuery, setSearchQuery] = useState("");

  function handleModal(beast) {
    setModal(!modal);
    setModalContent(beast);
  }

  function closeModal() {
    setModal(!modal);
    setModalContent({});
  }

  function handleBeasts(event) {
    let beastNum = parseInt(event.target.value);
    const filteredBeasts = data.filter((beast) => {
      const isHornMatch = beast.horns === beastNum || isNaN(beastNum);

      const query = searchQuery.toLowerCase();
      const title = beast.title.toLowerCase();
      const keyword = beast.keyword.toLowerCase();

      let titleMatch = false;
      let keywordMatch = false;

      for (let i = 0; i <= title.length - query.length; i++) {
        let j;
        for (j = 0; j < query.length; j++) {
          if (title[i + j] !== query[j]) {
            break;
          }
        }
        if (j === query.length) {
          titleMatch = true;
          break;
        }
      }

      for (let i = 0; i <= keyword.length - query.length; i++) {
        let j;
        for (j = 0; j < query.length; j++) {
          if (keyword[i + j] !== query[j]) {
            break;
          }
        }
        if (j === query.length) {
          keywordMatch = true;
          break;
        }
      }

      return isHornMatch && (titleMatch || keywordMatch);
    });

    event.target.value === ""
      ? setBeastData(data)
      : setBeastData(filteredBeasts);
  }

  return (
    <div className="App">
      <Header handleBeasts={handleBeasts} />
      <Main beastData={beastData} handleModal={handleModal} />
      {modal && <Modal modalContent={modalContent} closeModal={closeModal} />}
      <Footer />
    </div>
  );
}

export default App;
